import React,{useContext,createContext} from 'react'
import UseContext from './UseContext.js'
const UserContext=createContext({name:"vinay",place:'Haveri'})

function Funner(){
    const user=useContext(UserContext);
    return(
        <div>
            <h3>Hello world from Funner function</h3>
            <h3>This is {user.name} from {user.place}</h3>

        </div>
    )
}

function Fun(){
    const user=useContext(UserContext)
    const obj={name:"Ravi", place:'Bengaluru'}
    return(
        <div>
        <h3>I am {user.name} from {user.place}</h3>
        <UserContext.Provider val={obj} >
          <Funner />
        </UserContext.Provider>

        </div>

    )
}

export default function Context(){
  return (
    <div>
      <UserContext.Provider value={{name:"Rahul" ,place:"Jodhpur"}}> 
       <Fun />

      </UserContext.Provider>
    </div>
  )
}

export {UserContext}
