import React,{useReducer,useState} from 'react'


const TASKS={
    ADDTASK:"addTask",
    DELETETASK:'deleteTask'
}

function reducer(todos,action){
    switch(action.type){
        case TASKS.ADDTASK:
        return([...todos, action.input])

        case TASKS.DELETETASK:
        return([...todos])


        default:

    }


}

export default function Usereducer() {



    const [todos,dispatch]=useReducer(reducer, ["Learn_React","Learn_Node Js","First Learn DSA"]);
    const [inputvalue,setInputvalue]=useState("")


   function addTask(){
       console.log(inputvalue)
       dispatch({type:TASKS.ADDTASK, input:inputvalue})
   }
   function deleteTask(){
       dispatch({type:TASKS.DELETETASK, input:inputvalue})
   }

  return (
    <div style={{marginTop:"20px",marginLeft:"20px"}}>
     <input type='text' value={inputvalue} onChange={e=>setInputvalue(e.target.value)}
     onKeyDown={()=>{
        addTask
     }
     }/>
      <button onClick={addTask}>
          Add Task
      </button>
      <button>
          deleteTask
      </button>
      {/* <form> */}
      {todos.map((todo,i)=>{
        return (
            <h3>

                <input type='checkbox' id={i} name={`task${i}`} value={todo} />
                <label for={`task${i}`}>{todo}</label>
            </h3>
        )
    })}
    {/* </form> */}


    </div>
  )
}
