import React,{useReducer,useState} from 'react'


const TASKS={
    ADDTASK:"addTask",
    DELETETASK:'deleteTask',
    MOVEUP:"moveup",
    MOVEDOWN:"movedown"
}

function reducer(todos,action){
    switch(action.type){
      case TASKS.ADDTASK:
         return([...todos, action.input])

      case TASKS.DELETETASK:
           const newTodo=[];
           for(let i=0;i<todos.length;i++){
               newTodo.push(todos[i])
           }
           newTodo.splice(action.index, 1);
            return(newTodo)

      case TASKS.MOVEUP:

           if(action.index==0){
               return todos
           }
           else{
            const newTodo=[];
            for(let i=0;i<todos.length;i++){
                newTodo.push(todos[i])
            }
            [newTodo[action.index-1],newTodo[action.index]]=[newTodo[action.index],newTodo[action.index-1]]
              return (newTodo)
            }

      case TASKS.MOVEDOWN:
            if(action.index+1==todos.length){
                return todos
            }
            else{
             const newTodo=[];
             for(let i=0;i<todos.length;i++){
                 newTodo.push(todos[i])
             }
             [newTodo[action.index+1],newTodo[action.index]]=[newTodo[action.index],newTodo[action.index+1]]
               return (newTodo)
             }    

        default:
        return(todos)

    }


}

export default function Usereducer() {
    const localStorageTodos=(window.localStorage.getItem('todos'));
    let data=[];
    if(localStorage.length){
        data=JSON.parse(localStorageTodos);

    }

    const [todos,dispatch]=useReducer(reducer, data);
    const [inputvalue,setInputvalue]=useState("")
    window.localStorage.setItem("todos",JSON.stringify(todos))



   function addTask(){
       if(inputvalue.length){
        dispatch({type:TASKS.ADDTASK, input:inputvalue})
        setInputvalue("")
       } 

   }
   function deleteTask(i){
       dispatch({type:TASKS.DELETETASK, index:i})
   }
   function enterKeyPress(e){
      return(e.keyCode==13)
   }

   function moveUp(i){
     dispatch({type:TASKS.MOVEUP, index:i})
   }

   function moveDown(i){
    dispatch({type:TASKS.MOVEDOWN, index:i})
   }

  return (
    <div style={{marginTop:"20px",marginLeft:"20px"}}>
     <input type='text' value={inputvalue} onChange={e=>setInputvalue(e.target.value)}
     onKeyDown={(e)=>{
          if(enterKeyPress(e)){
              addTask()

          }
     }}
     placeholder='enter the task to add' />
      <button onClick={addTask}>
          Add Task
      </button>
      {todos.map((todo,i)=>{
        return (
            <h3 >

                <input type='checkbox' id={i} name={`task${i}`} value={todo}  />
                <span>{i}) </span>
                <label for={`task${i}`} style={{width:"100px" ,color:'red', marginRight:"10px",background:'lightGreen'}}>{todo}</label>
                <button id='up' onClick={()=>{
                      moveUp(i)
                }}>
                   ↑
                </button>
                <button id='down' onClick={()=>{
                    moveDown(i)
                }}>
                  ↓
                </button>
                <button style={{marginLeft:"20px"}} onClick={()=>{
                    deleteTask(i)
                    // console.log(i);

                }}>
                   delete
               </button>
            </h3>
        )
    })}      
    </div>
  )
}
