import './App.css';
import React,{useState} from 'react'
import {appContext} from './Components/Context.js'
import {First,Second,Third} from './Components'

const choices=['Bitcoin',"Etherium","Dogecoin","Litecoin","Chainlink"]

function App() {
   const [preferences,setPreferences]=useState({first:undefined, second :undefined, third:undefined })

   return(
     <div className="app">
       <appContext.Provider value={{preferences,setPreferences,choices}} >
       <div className="select">
       <First />
       <Second />
       <Third />
       </div>
       </appContext.Provider >
       <p></p>
     </div>
   )


}

export default App;
