import './App.css';
import React,{useState,useEffect,useContext,createContext} from 'react'
// import { colorContext,Colors }from './Components/colorContext.js'
// import {UseState,UseEffect,UseContext,Context,useReducer, Usereducer,Usereducer2} from './Components/'

import {useSpring,animated,useTransition} from 'react-spring'

function App() {
const [mount, setMount]=useState(false);  
const style2=useTransition(mount,{
    from:{x:-100, y:-100, opacity:0},
    enter:{x:200, y:200, opacity:1},
    leave:{x:-100,y:-100, opacity:0},
    delay:1000
})
// const styles=useSpring({
//   // loop:{reverse:true},
//   opacity:mount? 1:0,
//   marginTop:mount ? 300 :-300,
//   marginLeft:mount ? 300 :-300
// })

// const style2=useTransition()

  return (
    <div>
     <button onClick={()=>{
      setMount(!mount)
    }}>ClickHere</button>
    {transitions((style,item)=>{
       item:<animated.div style={style2}>
       <div className="box"></div>
      </animated.div>
    })}

    </div>
  );
}

export default App;
