import './App.css';
import React, { useState } from 'react'
import { appContext } from './Components/Context.js'
import { First, Second, Third } from './Components'

const choices = ['Bitcoin', "Etherium", "Dogecoin", "Litecoin", "Chainlink"]

function App() {
  const [preferences, setPreferences] = useState({ first: "Not Selected", second: "Not Selected", third: "Not Selected" })
  const [toggle, setToggle] = useState(false);

  function DropDownMenu() {
    return (
    <div className="form">
    <div className="select">
      <First />
      <Second />
      <Third />
    </div>
      <button className="sumbit_btn" onClick={() => {
        if (preferences.first == 'Not Selected' || preferences.second == 'Not Selected' || preferences.third == 'Not Selected') {
          window.alert("Please SelectAll Mendatary Fields")
        }
        else {
          window.alert("Your response has been recorded")
        }
      }}>Submit</button>
      <h2>{preferences.first}</h2>
      <h2>{preferences.second}</h2>
      <h2>{preferences.third}</h2>
      </div>  
    )
  }


  return (
    <div className="app">
      <appContext.Provider value={{ preferences, setPreferences, choices }} >
        <button onClick={() => {
          setToggle(!toggle);
        }} className="drop_btn">DropDown</button>


        {toggle ?  <DropDownMenu />: <div></div>}
      </appContext.Provider >

    </div>
  )


}

export default App;
