import React from 'react'
import { useState,useEffect } from 'react';
import '../Styles/UseState.css'



export default function UseState() {
  // const name='vinay'
  let [user,setUser]=useState({name:"vinay",place:"Haveri"});
  return (
    <div>
      <p>Name:</p>
      <input type='text' placeholder='enter your name' 
        onChange={
          (e)=>{
             setUser({...user, name:e.target.value})
          }
        }
      /> 
      <p>Place:</p>
      <input type='text' placeholder='enter your Place' 
        onChange={
          (e)=>{
            setUser({...user, place:e.target.value})
          }
        }
      />
      <button onClick={
        ()=>{
          // setUser({name:name})
          // console.log(user.name)
        }
      }>Click</button>
      <p>{user.name}</p>
      <p>{user.place}</p>

    </div>
  )
}
