function reducer(obj,action){
    switch(action.type){
        case 'increment':
        return{count:obj.count+1}

        case 'decrement':
        return{count:obj.count-1}

        default:
         return obj;
    }


}

export default function Usereducer() {



    const [todos,dispatch]=useReducer(reducer, ["Learn_React","Learn_Node Js","First Learn DSA"]);
    const array=todos.map((todo)=>{
        return (
            <h3>
                {todo} 
                <input type='checkbox' />
            </h3>
        )
    })



  return (
    <div style={{marginTop:"20px",marginLeft:"20px"}}>
      <button>
          Add Task
      </button>
      {array}


    </div>
  )
}
