import React,{useContext} from 'react'
import {appContext} from './Context.js'

import {DropDown} from './'
export default function Second() {
  const {choices, setPreferences,preferences}=useContext(appContext);
  if(preferences.first==preferences.second){
    setPreferences({...preferences, second:'Not Selected'})
  }

  const choicesLeft=choices.filter((choice)=>{
     return preferences.first!==choice
  })

  return (
    <div  className="choiceField">
      {choicesLeft.map((choice)=>{
         return(
           <h3 onClick={(e)=>{
            setPreferences({...preferences, second:e.target.innerHTML})
         }} className="choice">{choice}</h3>
         )
      })}  
    </div>
  )
}
