function App() {
const [mount, setMount]=useState(false);  
const styles=useSpring({
  // loop:{reverse:true},
  from:{opacity:0},
  to:{opacity:1}
})

  return (
    <div>
    <animated.div style={styles}>
    <div className="box"></div>
