  const choicesLeft=choices.filter((choice)=>{
    return (preferences.first!==choice)
 })

  return (
    <div className="choiceField">
       {choices.map((choice)=>{
         return(
           <h3 onClick={(e)=>{
              setPreferences({...preferences, first:e.target.innerHTML})
           }} className="choice">{choice}</h3>
         )
      })}  
    </div>
  )
}
